mapper.update:
DECLARE 
BEGIN	
	UPDATE PMS.APPROVAL
	   SET APPR_YN=#{apprYn}
		 , APPR_RSN=#{apprRsn}
	 WHERE APPR_ID=#{apprId};

	UPDATE PMS.APPROVAL
	   SET SEARCH_YN = 'Y' 
	 WHERE P_APPR_ID=#{apprId};

END;

update.foreach:
UPDATE PMS.APPROVAL
   SET P_APPR_ID=  #{approvalVO.pApprId}
 WHERE APPR_ID= #{approvalVO.apprId}
