mapper.update:
UPDATE PROJECT
SET DEL_YN = 'Y',
/* 수정자 추가 */
MDF_DT = SYSDATE
WHERE PRJ_ID = #{_parameter}

UPDATE PROJECT
   SET PRJ_NAME = #{prjName}
     , CLNT_INFO = #{clntInfo}
     , DEPT_ID = #{deptId}
     , STRT_DT = TO_DATE(#{strtDt},'YYYY-MM-DD')
     , END_DT = TO_DATE(#{endDt},'YYYY-MM-DD')
     , PRJ_STS = #{prjSts}
     , MDFR_ID = #{mdfId}  
     , MDF_DT = SYSDATE
     , PRJ_MEMO = #{prjMemo}
 WHERE PRJ_ID = #{prjId}

UPDATE PROJECT_TEAMMATE
SET DEL_YN = 'N'
WHERE PRJ_ID = #{prjId}
AND TM_ID = #{pmId}
AND ROLE = 'PM'

UPDATE PROJECT_TEAMMATE
SET DEL_YN = 'Y'
WHERE PRJ_TM_ID = #{_parameter}

UPDATE PROJECT_TEAMMATE
SET DEL_YN = 'N',
"ROLE" = #{role}
WHERE PRJ_TM_ID = #{prjTmId}

UPDATE PROJECT_TEAMMATE
SET RV_YN = 'Y'
WHERE PRJ_ID = #{prjId}
AND TM_ID = #{tmId}

UPDATE PROJECT_TEAMMATE
SET SRV_YN = 'Y'
WHERE PRJ_ID = #{prjId}
AND TM_ID = #{tmId}

    	UPDATE PROJECT_TEAMMATE
		   SET SRV_YN = 'Y'
		 WHERE PRJ_ID = #{surveyQuestionVO.prjId}
		   AND TM_ID = #{crtrId}
		   AND DEL_YN = 'N'

mapper.select:
    	SELECT PRJ_TM_ID 
		  FROM PROJECT_TEAMMATE
		 WHERE PRJ_ID = #{prjId}
		   AND TM_ID = #{tmId}
