mapper.update:
UPDATE SUPPLY 
   SET SPL_NAME = #{splName}
     , SPL_CTGR = #{splCtgr}
     , SPL_PRICE = #{splPrice}
     , INV_QTY = #{invQty}
     , SPL_IMG = #{splImg}
     , SPL_DTL = #{splDtl}
     , SPL_MDFR_ID = #{splMdfrId}
     , SPL_MDF_DT = SYSDATE
 WHERE SPL_ID = #{splId}

UPDATE SUPPLY
   SET DEL_YN = 'Y'
     , SPL_MDFR_ID = #{splMdfrId}
     , SPL_MDF_DT = SYSDATE
 WHERE SPL_ID = #{splId}
