mapper.select:
SELECT COUNT(1) FROM (SELECT * , REPLACE(SPL_NAME, ' ', '') SEARCH_SPL_NAME , REPLACE(SPL_CTGR, ' ', '') SEARCH_SPL_CTGR , REPLACE(#{searchKeyword}, ' ', '') AS SEARCH_KEYWORD FROM SUPPLY) WHERE DEL_YN = 'N';
AND SEARCH_SPL_NAME LIKE '%' || SEARCH_KEYWORD || '%'
AND SEARCH_SPL_CTGR LIKE '%' || SEARCH_KEYWORD || '%'
